										<a href="<?php echo $link_url; ?>" target="<?php echo $link_target; ?>"><span class="visually-hide-text"><?php echo $link_title; ?></span><i class="fab fa-youtube icon"></i></a>
									<?php elseif ($network == 'vimeo' && $link): ?>
